public class Punto1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1 - Declara dos variables numéricas (con el valor que desees), muestra por consola la suma,
		//resta, multiplicación, división y módulo (resto de la división).
		
		int num1=5;
		double num2=12.5;
		
		double suma=0;
		double resta=0;
		double mult=0;
		double div=0;
		double mod=0;
		
		suma=num1+num2;
		System.out.println("Resultado suma: "+suma);
		
		resta=num1-num2;
		System.out.println("Resultado resta: "+resta);
		
		mult=num1*num2;
		System.out.println("Resultado multiplicación: "+mult);
		
		div=num1/num2;
		System.out.println("Resultado división: "+div);
		
		mod=num1%num2;
		System.out.println("Resultado módulo: "+mod);
		
		
		
		

	}

}
